<?php	$nombre = $_POST['nombre'];	$apellido = $_POST['apellido'];	$edad = $_POST['edad'];    $ci = $_POST['ci'];	try{		 if(empty ($nombre)){		    die ('ERROR: El Nombre no puede ser vacio.');		 }		 if(empty ($apellido)){		    die ('ERROR: El Apellido no puede ser vacio.');		 }		 if(empty ($edad)){		    die ('ERROR: La Edad no puede ser vacia.');		 } else if ($edad < 18 || $edad >60){            die ('ERROR: La edad debe estar entre 18 y 60 aÃ±os.');         }		 if(empty ($ci)){		    die ('ERROR: La Cedula de Identidad no puede ser vacia o con valor 0.');		 } else if ($ci == '0'){            die ('ERROR: La Cedula de Identidad no puede ser 0.');         }		} catch (Exception $e){		echo "Problemas en el registro de personas";	}?><table width="200" border="1"><tr>    <td>NOMBRE</td>    <td>APELLIDO</td>    <td>EDAD</td>    <td>CEDULA DE IDENTIDAD</td></tr><tr>    <td><?php echo $nombre; ?></td>    <td><?php echo $apellido; ?></td>    <td><?php echo $edad; ?></td>    <td><?php echo $ci; ?></td></tr></table>